tinymce.PluginManager.add("grid", function(editor, url){
	
	editor.addButton("grid", {
		title: "Insert Grid Element(s)",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert Grid Element(s)",
				url: url+"/dialog.php",
				width: 400,
				height: 300,
				buttons: [
					{
						text: "Insert Grid Element(s)",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("embed").value),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	}),
	
	editor.addMenuItem("grid", {
		text: "Insert Grid Element(s)",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert Grid Element(s)",
				url: url+"/dialog.php",
				width: 400,
				height: 300,
				buttons: [
					{
						text: "Insert Grid Element(s)",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("embed").value),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	})
	
});