tinymce.PluginManager.add("sample", function(editor, url){
	
	editor.addButton("sample", {
		title: "Button Title",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Window Title",
				url: url+"/dialog.php",
				width: 400,
				height: 585,
				buttons: [
					{
						text: "OK Button",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent( win.getContentWindow().document.getElementById("embed").value ),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	}),
	
	editor.addMenuItem("sample", {
		text: "Menu Item Title",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Window Title",
				url: url+"/dialog.php",
				width: 400,
				height: 585,
				buttons: [
					{
						text: "OK Button",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent( win.getContentWindow().document.getElementById("embed").value ),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	})
	
});