tinymce.PluginManager.add("shortcode", function(editor, url){
	
	editor.addButton("shortcode", {
		title: "Insert a Module",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert a Module",
				url: url+"/dialog.php",
				width: 400,
				height: 200,
				buttons: [
					{
						text: "Insert Module",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("shortcode").value),
							win.close()
						}
					},
					{
						text: "Close",
						onclick: "close"
					}
				]
			})
		}
	}),
	
	editor.addMenuItem("shortcode", {
		text: "Insert a Module",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert a Module",
				url: url+"/dialog.php",
				width: 400,
				height: 200,
				buttons: [
					{
						text: "Insert Module",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("shortcode").value),
							win.close()
						}
					},
					{
						text: "Close",
						onclick: "close"
					}
				]
			})
		}
	})
	
});