tinymce.PluginManager.add("soundcloud", function(editor, url){
	
	editor.addButton("soundcloud", {
		title: "Insert a SoundCloud Embed",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert a SoundCloud Embed",
				url: url+"/dialog.php",
				width: 400,
				height: 290,
				buttons: [
					{
						text: "Insert SoundCloud Embed",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("embed").value),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	}),
	
	editor.addMenuItem("soundcloud", {
		text: "Insert a SoundCloud Embed",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert a SoundCloud Embed",
				url: url+"/dialog.php",
				width: 400,
				height: 585,
				buttons: [
					{
						text: "Insert SoundCloud Embed",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("embed").value),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	})
	
});