tinymce.PluginManager.add("vimeo", function(editor, url){
	
	editor.addButton("vimeo", {
		title: "Insert a Vimeo Video",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert a Vimeo Video",
				url: url+"/dialog.php",
				width: 400,
				height: 440,
				buttons: [
					{
						text: "Insert Video",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("embed").value),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	}),
	
	editor.addMenuItem("vimeo", {
		text: "Insert a Vimeo Video",
		icon: true,
		image: url+"/icon.png",
		context: "insert",
		onclick:function(){
			editor.windowManager.open({
				title: "Insert a Vimeo Video",
				url: url+"/dialog.php",
				width: 400,
				height: 440,
				buttons: [
					{
						text: "Insert Video",
						onclick:function(){
							var win = editor.windowManager.getWindows()[0];
							editor.insertContent(win.getContentWindow().document.getElementById("embed").value),
							win.close()
						}
					},
					{
						text: "Cancel",
						onclick: "close"
					}
				]
			})
		}
	})
	
});